collectionProcessors.formula:
(
( {!productsToHaveTransferSelection.firstSelectedRow.FinServ__RecordTypeName__c} = "SIPP" 
&& 
( CONTAINS({!currentItem_sourceProduct.FinServ__RecordTypeName__c}, "Pension") || CONTAINS({!currentItem_sourceProduct.FinServ__RecordTypeName__c}, "GPP") || CONTAINS({!currentItem_sourceProduct.FinServ__RecordTypeName__c}, "AVC")
|| 
ISPICKVAL({!currentItem_sourceProduct.Subtype__c}, "Defined Contribution") )
)
||
(
{!productsToHaveTransferSelection.firstSelectedRow.FinServ__RecordTypeName__c} = "Stocks & Shares ISA"
&& 
(CONTAINS({!currentItem_sourceProduct.FinServ__RecordTypeName__c}, "Cash ISA") || CONTAINS({!currentItem_sourceProduct.FinServ__RecordTypeName__c}, "Peer-Peer / Innovative finance"))
)
||
(
{!currentItem_sourceProduct.FinServ__RecordTypeName__c} = {!productsToHaveTransferSelection.firstSelectedRow.FinServ__RecordTypeName__c}
)
)
&&
(
{!currentItem_sourceProduct.FinServ__PrimaryOwner__c} = {!productsToHaveTransferSelection.firstSelectedRow.FinServ__PrimaryOwner__c}
)
